mapper.select:
SELECT DISTINCT employee_id
FROM base_employee_role_rel where role_id in

    #{id}

SELECT r.* FROM base_role r left join base_employee_role_rel er on r.id = er.role_id where er.employee_id = #{employeeId}
and r.code in
#{id}
and r.state = 1 UNION SELECT r.* FROM base_role r left join base_org_role_rel orr on orr.role_id = r.id left join base_org o on o.id = orr.org_id left join base_employee_org_rel eo on eo.org_id = o.id where eo.employee_id = #{employeeId}
and r.code in
#{id}
and r.state = 1 and o.state = 1
