mapper.select:
select

from base_role_resource_rel r
left join base_role role on role.id = r.role_id
where r.role_id = #{roleId}
and role.category = #{category}

SELECT DISTINCT rrr.resource_id
FROM base_role_resource_rel rrr

    and rrr.role_id = #{roleId}

        and rrr.application_id = #{applicationId}

select DISTINCT r.resource_id from base_role_resource_rel r left join base_role role on role.id = r.role_id left join base_employee_role_rel er on er.role_id = role.id where er.employee_id = #{employeeId}
and r.application_id = #{applicationId}
and role.state = 1 union select DISTINCT r.resource_id from base_role_resource_rel r left join base_role role on role.id = r.role_id left join base_org_role_rel orr on orr.role_id = role.id left join base_org o on o.id = orr.org_id left join base_employee_org_rel eo on eo.org_id = o.id where eo.employee_id = #{employeeId}
and r.application_id = #{applicationId}
and role.state = 1 and o.state= 1
